INSERT INTO `hp_about_translations` VALUES (6, 3, 'Null', 'Pesos convertibles (CUC) is the only accepted currency in Cuba. You can get it from Euros, sterling pounds, Canadian dollars, Mexican pesos or Swiss francs.', 'en', 'assets/images/flags/260-united-kingdom.png');
