INSERT INTO `hp_hostales` VALUES (1, '+53 7 8601257 / +53 7 8618027', '23.113417', '-82.413297', 'info@hostalperegrino.com / hostalperegrino@gmail.com', 'Hostal Peregrino Consulado', 'Hostal con cinco habitaciones y tres apartamentos adjuntos, situado muy cerca de Prado y La Habana Vieja.', 'Hostal con cinco habitaciones y tres apartamentos adjuntos, situado muy cerca de Prado y La Habana Vieja. Dos de las habitaciones son dúplex, para familias o grupos, con dos niveles: en el primero tiene una cama doble y en el segundo dos camas personales. Habitaciones con balcón, aire acondicionado, baño privado con agua caliente 24 horas y secadora de pelo, TV, minibar, caja de seguridad y equipo de música.', 'Consulado, 152 – 1er piso – entre Colón y Trocadero.Centro Habana. La Habana. Cuba', '2019-12-07 07:16:14', '2019-12-07 07:16:14', 1);
INSERT INTO `hp_hostales` VALUES (2, '+53 7 8601257 / +53 7 8618027', '23.105785', '-82.439827', 'info@hostalperegrino.com / hostalperegrino@gmail.com', 'Hostal Peregrino Lealtad', 'Está situado en el centro de La Habana (Centro Habana), al lado del Malecón (la avenida principal de la ciudad), entre las dos principales áreas de interés turísticos: La Habana Vieja y Vedado (La Rampa).', 'Está situado en el centro de La Habana (Centro Habana), al lado del Malecón (la avenida principal de la ciudad), entre las dos principales áreas de interés turísticos: La Habana Vieja y Vedado (La Rampa), a una distancia que permite ir caminando a ambas zonas y muy cerca de Casa de la Música, Academia de salsa, zona comercial, etc. Cuenta con dos habitaciones con terraza, aire acondicionado, baño privado con agua caliente 24 horas y secadora de pelo, TV, minibar, caja de seguridad y radio/reloj-despertador.', 'Lealtad, 203 -1er piso – entre Concordia y Virtudes.Centro Habana.', '2019-12-07 07:22:28', '2019-12-07 07:22:28', 1);
INSERT INTO `hp_hostales` VALUES (3, '+53 7 8601257 / +53 7 8618027', '23.093941', '-82.439827', 'info@hostalperegrino.com / hostalperegrino@gmail.com', 'Hostal Peregrino El Encinar', 'Se encuentra ubicado en el corazón de la Habana Vieja, a unos metros de la Plaza de la Catedral.', 'Se encuentra ubicado en el corazón de la Habana Vieja, a unos metros de la Plaza de la Catedral. Cuenta con cuatro habitaciones con aire acondicionado, baño privado, agua caliente 24 horas, secadora de pelo, minibar, TV, caja de seguridad y radio/reloj-despertador. La terraza del hostal tiene vista al centro histórico y a la entrada de la bahía y a las fortalezas.', 'Chacon, #60 1er y 2do piso – entre Cuba y Aguiar.Habana Vieja. La Habana. Cuba.', '2019-12-07 07:24:50', '2020-02-10 01:52:31', 0);
INSERT INTO `hp_hostales` VALUES (4, '+53 7 8601257 / +53 7 8618027', '27.113417', '-82.439827', 'info@hostalperegrino.com / hostalperegrino@gmail.com', 'Hostal Peregrino Vedado', 'Hostal situado en el corazón del Vedado, una de las dos áreas turísticas de la ciudad.', 'Hostal situado en el corazón del Vedado, una de las dos áreas turísticas de la ciudad. Está muy cerca de la zona de vida nocturna (La Rampa) con cabarets, bares y clubes, Plaza de la Revolución, Universidad de La Habana y del malecón. Tiene cuatro habitaciones con aire acondicionado, baño privado con agua caliente 24 horas y secadora de pelo, TV, minibar, caja de seguridad y radio/reloj-despertador. Tiene dos agradables terrazas para ser usadas como áreas de descanso.', 'Calle 9na,406 entre G y F. Vedado. La Habana. Cuba', '2019-12-07 07:27:25', '2019-12-19 22:56:17', 1);

