INSERT INTO `hp_hostales_translations` VALUES (1, 1, 'Hostal Peregrino Consulado', 'Hostal con cinco habitaciones y tres apartamentos adjuntos, situado muy cerca de Prado y La Habana Vieja.', 'Hostal con cinco habitaciones y tres apartamentos adjuntos, situado muy cerca de Prado y La Habana Vieja. Dos de las habitaciones son dúplex, para familias o grupos, con dos niveles: en el primero tiene una cama doble y en el segundo dos camas personales. Habitaciones con balcón, aire acondicionado, baño privado con agua caliente 24 horas y secadora de pelo, TV, minibar, caja de seguridad y equipo de música.', 'Consulado, 152 – 1er piso – entre Colón y Trocadero.Centro Habana. La Habana. Cuba', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_hostales_translations` VALUES (2, 1, 'Hostal Peregrino Consulado', 'The hostal has five rooms and three attached apartments, very close to Prado, and Havana Vieja.', 'The hostal has five rooms and three attached apartments, very close to Prado, and Havana Vieja. Rooms with balcony, air conditioner, own bath and 24 hours hot water, hairdryer , TV, minibar, safe box and stereo. Two of the rooms are duplex, suitable for families or groups. These room has two levels: one double bed on the first and two single beds in the second, with the same amenities of the other rooms of the hostal.', 'Consulado, 152 – 1st floor – between Colón and Trocadero.Centro Habana. La Habana. Cuba', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_hostales_translations` VALUES (3, 2, 'Hostal Peregrino Lealtad', 'Está situado en el centro de La Habana (Centro Habana), al lado del Malecón (la avenida principal de la ciudad), entre las dos principales áreas de interés turísticos: La Habana Vieja y Vedado (La Rampa).', 'Está situado en el centro de La Habana (Centro Habana), al lado del Malecón (la avenida principal de la ciudad), entre las dos principales áreas de interés turísticos: La Habana Vieja y Vedado (La Rampa), a una distancia que permite ir caminando a ambas zonas y muy cerca de Casa de la Música, Academia de salsa, zona comercial, etc. Cuenta con dos habitaciones con terraza, aire acondicionado, baño privado con agua caliente 24 horas y secadora de pelo, TV, minibar, caja de seguridad y radio/reloj-despertador.', 'Lealtad, 203 -1er piso – entre Concordia y Virtudes.Centro Habana.', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_hostales_translations` VALUES (4, 2, 'Hostal Peregrino Lealtad', 'The hostal has five rooms and three attached apartments, very close to Prado, and Havana Vieja.', 'The hostal has five rooms and three attached apartments, very close to Prado, and Havana Vieja. Rooms with balcony, air conditioner, own bath and 24 hours hot water, hairdryer , TV, minibar, safe box and stereo. Two of the rooms are duplex, suitable for families or groups. These room has two levels: one double bed on the first and two single beds in the second, with the same amenities of the other rooms of the hostal.', 'Consulado, 152 – 1st floor – between Colón and Trocadero.Centro Habana. La Habana. Cuba', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_hostales_translations` VALUES (5, 3, 'Hostal Peregrino El Encinar', 'Se encuentra ubicado en el corazón de la Habana Vieja, a unos metros de la Plaza de la Catedral.', 'Se encuentra ubicado en el corazón de la Habana Vieja, a unos metros de la Plaza de la Catedral. Cuenta con cuatro habitaciones con aire acondicionado, baño privado, agua caliente 24 horas, secadora de pelo, minibar, TV, caja de seguridad y radio/reloj-despertador. La terraza del hostal tiene vista al centro histórico y a la entrada de la bahía y a las fortalezas.', 'Chacon, #60 1er y 2do piso – entre Cuba y Aguiar.Habana Vieja. La Habana. Cuba.', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_hostales_translations` VALUES (6, 3, 'Hostal Peregrino El Encinar', 'The hostal has five rooms and three attached apartments, very close to Prado, and Havana Vieja.', 'The hostal has five rooms and three attached apartments, very close to Prado, and Havana Vieja. Rooms with balcony, air conditioner, own bath and 24 hours hot water, hairdryer , TV, minibar, safe box and stereo. Two of the rooms are duplex, suitable for families or groups. These room has two levels: one double bed on the first and two single beds in the second, with the same amenities of the other rooms of the hostal.', 'Consulado, 152 – 1st floor – between Colón and Trocadero.Centro Habana. La Habana. Cuba', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_hostales_translations` VALUES (7, 4, 'Hostal Peregrino Vedado', 'Hostal situado en el corazón del Vedado, una de las dos áreas turísticas de la ciudad.', 'Hostal situado en el corazón del Vedado, una de las dos áreas turísticas de la ciudad. Está muy cerca de la zona de vida nocturna (La Rampa) con cabarets, bares y clubes, Plaza de la Revolución, Universidad de La Habana y del malecón. Tiene cuatro habitaciones con aire acondicionado, baño privado con agua caliente 24 horas y secadora de pelo, TV, minibar, caja de seguridad y radio/reloj-despertador. Tiene dos agradables terrazas para ser usadas como áreas de descanso.', 'Calle 9na,406 entre G y F. Vedado. La Habana. Cuba', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_hostales_translations` VALUES (8, 4, 'Hostal Peregrino Vedado', 'This hostal is located in Vedado, one of the most important touristic areas of La Habana: very close to the nightlife zone, cabarets, bars, clubs (La Rampa), Plaza de la Revolucion, University and Malecon.', 'This hostal is located in Vedado, one of the most important touristic areas of La Habana: very close to the nightlife zone, cabarets, bars, clubs (La Rampa), Plaza de la Revolucion, University and Malecon. Four air conditioned rooms with private bathroom and 24 hours hot water, hairdryer , TV, minibar, safe box and radio/clock-alarm. The hostal has also two nice terraces as realaxation areas.', '9th Street, 406 between G and F. Vedado. La Habana. Cuba', 'en', 'assets/images/flags/260-united-kingdom.png');
