INSERT INTO `hp_services_translations` VALUES (1, 4, 'Servicio de recogida al aereopuerto', 'Incluye la recogida por un taxista profesional en un auto oficial. Es un modo fácil, seguro y personalizado de comenzar el viaje en Cuba. Será recibido en el lobby del aeropuerto por una persona con un cartel con sus nombres escritos en él. Precio del servicio desde el aeropuerto de La Habana: 30.00 CUC (el precio ya incluye la espera en el aeropuerto). Para recogida desde otros aeropuertos debe contarnos directamente.', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_services_translations` VALUES (2, 4, 'Picking up service', 'A professional driver with official license will pick you up at the airport. It is a safe, easy and customized beginning. If you choose this option you will be met at the airport lobby, where you must look for the person who has a poster with your names. Price of the service from Havana airport: 30.00 CUC one way (the price includes the waiting time). For airport picking up service from other airport, please, contact us directly.', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_services_translations` VALUES (3, 5, 'Servicio de recogida en la estación de autobús', 'Incluye la recogida por un taxista oficial en la estación de autobús para su traslado al hostal. Será recibido por el taxista a la salida de la estación con el cartel HOSTAL PEREGRINO y su nombre escrito. Precio del servicio: 10.00 CUC.', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_services_translations` VALUES (4, 5, 'Pick up service at the bus station', 'Includes the collection by an official taxi driver at the bus station for transfer to the hostel. You will be received by the taxi driver at the exit of the station with the HOSTAL PEREGRINO sign and your written name. Price of the service: 10.00 CUC.', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_services_translations` VALUES (5, 6, 'Desayuno', 'El desayuno es un buffet que incluye: jugos, frutas, pan, mantequilla y miel, café, leche, té, yogurt, chocolate, vegetales, pancake, jamón, tortilla, pastel/bizcochos. El precio es 5.00 CUC por persona.', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_services_translations` VALUES (6, 6, 'Breakfast', 'Available on request. Breakfast includes juice, fruits, bread, butter, honey, pastry, milk, coffee, tea, yogurt, omelette, ham, vegetables, hot chocalate, and pancakes. The price is 4.00 CUC per person.', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_services_translations` VALUES (7, 7, 'Lavado', 'El precio del servicio depende del volumen de la ropa a lavar.', 'es', 'assets/images/flags/128-spain.png');
INSERT INTO `hp_services_translations` VALUES (8, 7, 'Laundry', 'The price of the service is per amount of clothe.', 'en', 'assets/images/flags/260-united-kingdom.png');
INSERT INTO `hp_services_translations` VALUES (9, 8, 'Coctelería', NULL, 'es', 'assets/images/flags/128-spain.png');
